import "./App.css";
import banner from "./banner.png";
import squarespace from "./squarespace.svg";
import React, { useState, useEffect } from "react";
import { Heading } from "./components/Heading";
import { UnsplashImage } from "./components/UnsplashImage";
import { Loader } from "./components/Loader";
import axios from "axios";
import InfiniteScroll from "react-infinite-scroll-component";

import styled from "styled-components";
import { createGlobalStyle } from "styled-components";

function App() {
  const [images, setImage] = useState([]);

  const [images2, setimage2] = React.useState([]);
  const [images3, setimage3] = React.useState([]);
  const [index, setindex] = React.useState([1]);

  useEffect(() => {
    FetchImages();
  }, []);

  const FetchImages = (count = 10, index) => {
    const apiRoot = "https://api.unsplash.com";
    const accessKey = process.env.REACT_APP_ACCESSKEY;

    axios
      .get(
        `https://api.unsplash.com/search/photos?page=${index}&query=office&per_page=30&client_id=DXYD_GyjDUwagXbKcj_4HvYGwkxDp8HKCkwXUJ12rT8`
      )
      .then((res) => {
        // console.log(res.data.results);
        setImage([...images, ...res.data.results]);
      });
    axios
      .get(
        `https://api.unsplash.com/search/photos?page=${index +1}&query=office&per_page=30&client_id=DXYD_GyjDUwagXbKcj_4HvYGwkxDp8HKCkwXUJ12rT8`
      )
      .then((res) => {
        // console.log(res.data.results);
        setimage2([...images, ...res.data.results]);
      });
  };
  // var index = 1;
  // function fetchdata(index) {

  // }
  // for (index; index < 2; index++) {
  //   fetch(
  //     `https://api.unsplash.com/search/photos?page=${index}&query=office&per_page=30&client_id=DXYD_GyjDUwagXbKcj_4HvYGwkxDp8HKCkwXUJ12rT8`
  //   )
  //     .then((res) => res.json())
  //     .then((res) => setitem(res.results));
  //   fetch(
  //     `https://api.unsplash.com/search/photos?page=${index + 1
  //     }&query=office&per_page=30&client_id=DXYD_GyjDUwagXbKcj_4HvYGwkxDp8HKCkwXUJ12rT8`
  //   )
  //     .then((res) => res.json())
  //     .then((res) => setitem1(res.results));
  //   fetch(
  //     `https://api.unsplash.com/search/photos?page=${index + 2
  //     }&per_page=30&query=office&client_id=DXYD_GyjDUwagXbKcj_4HvYGwkxDp8HKCkwXUJ12rT8`
  //   )
  //     .then((res) => res.json())
  //     .then((res) => setitem2(res.results));
  // }

  return (
    <>
      <div className="top">
        <div className="top_data">
          <h1>Unsplash</h1>
          <p>The internetâ€™s source of freely-usable images. </p>
          <p> Powered by creators everywhere.</p>

          <div className="search_box">
            <input
              type="text"
              placeholder="Search free high-resolution photos"
            />
          </div>
        </div>
        <div className="banner">
          <img src={banner} alt="" />
        </div>
        <div className="bottom">
          <div className="first1">
            <p>Photo by Stephan Leonardi</p>
          </div>
          <div className="second2">
            <p>Read more about the Unsplash License</p>
          </div>
          <div className="third_3">
            <div className="third3">
              <img src={squarespace} alt="" />
            </div>
            <p>All you need to create a website</p>
          </div>
        </div>
      </div>
      <div className="main">
        <div className="App">
          <div className="image_box">
            {images?.map((item) => (
              <div key={item.id} className="card">
                <div className="top2">
                  <p>+</p>
                </div>
                <img src={item.urls.regular} alt="" />
                <a href={item.links.download_location}> Download Image</a>
              </div>
            ))}
          </div>
          <div className="image_box">
            <Heading />
            <InfiniteScroll
              dataLength={images.length}
              next={FetchImages}
              hasMore={true}
              loader={<Loader />}
            >
              <>
                {images2?.map((image) => (
                  <div key={image.id} className="card">
                    {/* <div className="top4">
                      <p>+</p>
                    </div> */}
                    <img src={image.urls.small} alt="" />
                  </div>
                ))}
              </>
            </InfiniteScroll>
          </div>
          <div className="image_box"></div>
        </div>
      </div>
    </>
  );
}

export default App;

